mapper.select:
SELECT


,shebei.shebei_name as shebeiName
,shebei.shebei_uuid_number as shebeiUuidNumber
,shebei.shebei_photo as shebeiPhoto
,shebei.shebei_types as shebeiTypes
,shebei.shebei_kucun_number as shebeiKucunNumber
,shebei.shebei_old_money as shebeiOldMoney
,shebei.shebei_new_money as shebeiNewMoney
,shebei.shebei_clicknum as shebeiClicknum
,shebei.shebei_content as shebeiContent
,shebei.shangxia_types as shangxiaTypes
,shebei.shebei_delete as shebeiDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney

FROM shebei_collection  a
left JOIN shebei shebei ON a.shebei_id = shebei.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.shebei_id = #{params.shebeiId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.shebei_collection_types = #{params.shebeiCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.shebei_id IS NOT NULL


        and shebei.shebei_name like CONCAT('%',#{params.shebeiName},'%')


        and shebei.shebei_uuid_number like CONCAT('%',#{params.shebeiUuidNumber},'%')


        and shebei.shebei_types = #{params.shebeiTypes}



          and shebei.shebei_kucun_number >= #{params.shebeiKucunNumberStart}   


          and shebei.shebei_kucun_number <= #{params.shebeiKucunNumberEnd}   


        and shebei.shebei_kucun_number = #{params.shebeiKucunNumber}


          and shebei.shebei_old_money >= #{params.shebeiOldMoneyStart}   


          and shebei.shebei_old_money <= #{params.shebeiOldMoneyEnd}   


          and shebei.shebei_new_money >= #{params.shebeiNewMoneyStart}   


          and shebei.shebei_new_money <= #{params.shebeiNewMoneyEnd}   


          and shebei.shebei_clicknum >= #{params.shebeiClicknumStart}   


          and shebei.shebei_clicknum <= #{params.shebeiClicknumEnd}   


        and shebei.shebei_clicknum = #{params.shebeiClicknum}


        and shebei.shebei_content like CONCAT('%',#{params.shebeiContent},'%')


        and shebei.shangxia_types = #{params.shangxiaTypes}



          and shebei.shebei_delete >= #{params.shebeiDeleteStart}   


          and shebei.shebei_delete <= #{params.shebeiDeleteEnd}   


        and shebei.shebei_delete = #{params.shebeiDelete}


          and UNIX_TIMESTAMP(shebei.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(shebei.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


order by a.${params.sort} ${params.order}
